import { Component } from "react";

class Introduction extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIntroduction: false,
    };
  }
  setactiveIntroduction() {
    if (this.state.activeIntroduction) {
      var display = document.getElementById("Introduction");
      display.style.display = "flex";
    }
    else{
        return this.props.active
    }
  }
  render() {
    return (
      <div className="Introduction" style={{display:"flex"}} id="Introduction">
        <h1>My name is nutthaphong wongnan {this.setactiveIntroduction}</h1>
        <ul>
          <li>Age : 22 year old</li>
          <li>Study year : third year</li>
          <li>Faculty : Faculty of scince & Computer scince</li>
          <li>City : changmai capitalcity / 5000 / marim</li>
        </ul>
      </div>
    );
  }
}
export default Introduction
