import { Component } from "react";
import ReactTypingEffect from "react-typing-effect";
import TestSlide from "../component/testSlide.js"
import Backgorud from "../img/backgorud.jpg"
import Tylerdurden from "../img/Tyler-durden-1.jpg"
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      senDisplayref: false,
    };
  }
  activeDisplayref = () => {
    if (this.state.senDisplayref === false) {
      this.setState({
        senDisplayref: true
      });
      console.log("if")
      document.getElementById("Displayref").style.display = "inline-block";
    } else {
      this.setState({
        senDisplayref: false
      });
      console.log("else")
      document.getElementById("Displayref").style.display = "none";
    }
  };
  render() {
    return (
      <div className="Home-card">
        <div>
          <ReactTypingEffect
            text="WELCOME TO MY PERSONAL PROFILE.."
            speed={80}
            eraseDelay={200}
          ></ReactTypingEffect>
        </div>
        <div>
          <p>about my personal profile..</p>
        </div>
        <button onClick={this.activeDisplayref}>Get Started</button>
        <p>{this.state.senDisplayref}</p>
        <div
          className="Displayref_element"
          id="Displayref"
          style={{display : "none"}}
        >
        </div>
      </div>
    );
  }
}
export default Home;
