import { Component } from "react";
import React  from "react";
import NavItem from "./NavItem"

class Navbar extends Component{
    constructor(props){
        super(props)
        this.state = {
            "NavbarActive": ""
        }
    }
    activeitem(item){
        if(this.state.NavbarActive.length > 0)
        {document.getElementById(this.state.NavbarActive.classList.remove('active'));}
        this.setState({"NavItemActive": item},() => {
            document.getElementById(this.state.NavbarActive.classList.add('active'))
        })
    }
    render(){
        return(
            <nav>
                <ul>
                    <NavItem item="Home" tolink="/" activeitem={this.activeitem}></NavItem>
                    <NavItem item="About" tolink="/About" activeitem={this.activeitem}></NavItem>
                    <NavItem item="Education" tolink="/Education" activeitem={this.activeitem}></NavItem>
                    <NavItem item="Contact" tolink="/Contact" activeitem={this.activeitem}></NavItem>
                    <NavItem item="Skill" tolink="/Skill" activeitem={this.activeitem}></NavItem>
                </ul>
            </nav>
        )
    }
}

export default Navbar;
