import { Component } from "react";
class SlideShow extends Component{
    slideIndex = 1;
    test(){
        let slide = document.getElementsByClassName("Slideshow")
    }
    showSlide(n){
        let slides = document.getElementsByClassName("Slideitem");
        let dots = document.getElementsByClassName("dot");
        if(n > slides.length){
            this.slideIndex = 1;
        }
        if(n < 1){
            this.slideIndex = slides.length
        }
        for(let i = 0; i < slides.length ; i++){
            slides[i].style.display = "none"
        }
        for(let i =0; i < dots.length ; i ++){
            dots[i].className = dots[i].className.replace(" active","")
        }
        slides[this.slideIndex-1].style.diplay = "block"
        dots[this.slideIndex-1].className += " active"
    }
    pluseSlide(index){
        this.showSlide(this.slideIndex += index)
    
    }


    render(){
      return(
        <>
        <div className="Slideshowcontainer">
            <div className="Slideitem">
                <p>here</p>
            </div>
            <div className="Slideitem">
                <p>here</p>
            </div>
            <div className="Slideitem">
                <p>here</p>
            </div>
            <div className="Slideitem">
                <p>here</p>
            </div>
            <a class="prev" >&#10094;</a>
            <a class="next">&#10095;</a>
        </div>
        <br></br>
        <div class="dots" style={{textAlign:"center"}}>
            <span class="dot"></span>
            <span class="dot"></span>
            <span class="dot"></span>
        </div>
        </>
      )
    }
}
export default SlideShow
