import { Component } from "react";
import ReactTypingEffect from "react-typing-effect";
import Introduction from "../component/Introduction";
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      senDisplayref: false,
    };
  }
  activeDisplayref = () => {
    if (!this.state.senDisplayref) {
      this.setState({
        senIntoduction: true,
      });
      document.getElementById("Displayref").style.display = "inline-block";
    } else {
      this.setState({
        senIntoduction: true,
      });
      document.getElementById("Displayref").style.display = "none";
    }
  };
  render() {
    return (
      <div className="Home-card">
        <div>
          <ReactTypingEffect
            text="WELCOME TO MY PERSONAL PROFILE.."
            speed={80}
            eraseDelay={200}
          ></ReactTypingEffect>
        </div>
        <div>
          <p>about my personal profile..</p>
        </div>
        <button onClick={this.activeDisplayref}>Get Started</button>
        <div
          className="Displayref_element"
          id="Displayref"
          style={{ display: "none" }}
        >
          <Introduction></Introduction>
        </div>
      </div>
    );
  }
}
export default Home;
