import React, { Component } from 'react';

class MyComponent extends Component {
  constructor(props) {
    super(props);
    // Initialize state in the constructor
    this.state = {
      senIntoduction: false
    };
  }

  // Function to update the state
  setSenIntroduction = () => {
    // Use setState to update the state
    this.setState({
      senIntoduction: true
    });
  }

  render() {
    return (
      <div>
        <p>{this.state.senIntoduction ? 'Introduction is shown' : 'Introduction is hidden'}</p>
        <button onClick={this.setSenIntroduction}>Show Introduction</button>
      </div>
    );
  }
}

export default MyComponent;
